import { useState, useEffect } from "react";
import Bloglist from "./Blocklist";

function Blog(){
    // creating an array of objects with use states
    let [blogs, setBlog] = useState(/*[
        {title: "first blog post", body: "lorem blah blah blah", author: "Joseph", id: 1},
        {title: "Second blog post", body: "lorem blah blah blah", author: "Richard", id: 2},
        {title: "third blog post", body: "lorem blah blah blah", author: "Simeon", id: 3}
    ] */ null);

    function deleteBlog(id){
        let newBlog = blogs.filter((blog) => blog.id !== id);
        setBlog(newBlog);
    }

    // passing array of state change to trigger use Effect
    useEffect(() => {
        //console.log("Use effect ran")
        fetch('http://localhost:8000/blogpost')
        .then()
    }, [blogs])
    return(
        // sending usestate variable Blog as a props
        <article >
            < Bloglist pBlog = {blogs} title="Blogs list" deleteBlog={deleteBlog}/>
            {/* using filter to send only some of the props data */}
            {/* < Bloglist pBlog = {blogs.filter((blog) => blog.author === "Joseph")}  title = "Joseph's blog list"/> */}
        </article>
    )
}

export default Blog;
