import { useState } from "react";
import { useHistory } from "react-router-dom";

const Addblog = () => {
    // creating a state to keep track of inputs
    let [title, setTitle] = useState('');
    let [body, setBody] = useState('');
    const [author, setAuthor] = useState('joseph');
    const [ispending, setIspending] = useState(false); //state variable to change button

    // using history hook to go back to a component (or page)
    const history = useHistory();

    const submitForm = (e) => {
        setIspending(true);
        e.preventDefault();
        let newBlog = {title, body, author};
        // creating a post request
        fetch('http://localhost:8000/blogpost', {
            method: 'POST',
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(newBlog)
        }) .then(() => {
            console.log('blog added');
            setIspending(false);
            history.go()
        })
    }
    return (
        <section className="addContainer">
            <h2>Add a new Blog</h2>
            <form onSubmit={submitForm}>
                <label htmlFor="">Blog Title</label>
                <input
                    type="text"
                    required
                    value={title}
                    onChange = {(e) => setTitle(e.target.value)} // keeping track of input value
                />
                <label>Blog body</label>
                <textarea
                    required
                    value={body}
                    onChange = {(e) => setBody(e.target.value)}
                ></textarea>
                <label>Blog author:</label>
                <select
                    value={author}
                    onChange = {(e) => setAuthor(e.target.value)}
                >
                    <option value="joseph">Joseph</option>
                    <option value="sarah">Sarah</option>
                </select>
                {!ispending && <button>Add Blog</button>} {/* condition for which button to add */}
                {ispending && <button>Adding blog...</button>}
            </form>
            <p>{title}</p>
            <p>{body}</p>
            <p>{author}</p>
        </section>
     );
}

export default Addblog;
