import './App.css';
import Navbars from './Navbar';
import Homepage from './Homepage';
import Blog from './Blog';
//using react-dom
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import About from './About';
import BlogDetails from './BlogDetails';
import Addblog from './Addblog';
import Notfound from './Notfound';

function App() {
  return (
    <Router>
      <div className="container">
        <Navbars />
        <Switch>
          <Route exact path='/'>
            <Homepage />
            <Blog />
          </Route>
          <Route path='/about'>
            <About />
          </Route>
          <Route path='/blogs/:id'> {/*  a page based on different id */}
            <BlogDetails />
          </Route>
          <Route path='/add'>
            <Addblog />
          </Route>
          <Route path='*'> {/* adding 404 error for a page that can't be found*/}
            <Notfound />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
