import { useState } from "react";

const ShortLink = ( { list, copy} ) => {
    const [copiedStatus, setCopiedStatus] = useState({});
    // function calls the copy to cliboard function passed as prop
    //and also handle copied button
    const handleCopy = (link) => {
        copy(link);
        setCopiedStatus((prevStatus) => ({
          ...prevStatus,
          [link]: true,
        }));
      };
    return ( 
        <>
            {list.map((link) => (
                <div 
                className=" bg-white w-10/12 mx-auto my-8 grid"
                key={link.key}>
                    <p className="truncate border-b border-gray p-4">{link.full}</p>
                    <p className="p-4 text-cYan">{link.short}</p>
                    {copiedStatus[link.short] ? (
                        <button disabled>copied</button>
                    ) : (
                        <button 
                        className=" bg-cYan text-center py-3 w-10/12 justify-self-center rounded-md text-white font-bold"
                        onClick={() => handleCopy(link.short)}>Copy</button>
                    )}
                </div>
            ))}
        </>
    );
}

export default ShortLink;
