import Bloglist from "./Blocklist";
import useFetch from "./useFetch";

function Blog(){

    /*function deleteBlog(id){
        let newBlog = blogs.filter((blog) => blog.id !== id);
        setBlog(newBlog);
    } */
    //colee
    let {datas: blogs, isloaded, errors} = useFetch('http://localhost:8000/blogpost')
    return(
        // sending usestate variable Blog as a props
        <article >
            { errors && <div>{ errors }</div> }
            { isloaded && <div>Loading...</div> }
            {blogs && < Bloglist pBlog = {blogs} title="Blogs list"/> }
            {/* using filter to send only some of the props data */}
            {/* < Bloglist pBlog = {blogs.filter((blog) => blog.author === "Joseph")}  title = "Joseph's blog list"/> */}
        </article>
    )
}

export default Blog;
