// props is added as an argument to recieve data
function Bloglist(props) {
    // assigning data from props to a constant
    const blogs = props.pBlog;
    return(
        //using map function to iterate and display array content
        <section className="BlogContain">   
            <h1>{props.title}</h1>
            {blogs.map((blog) => (
                <div className="container" key={blog.id}>
                    <h2>{blog.title}</h2>
                    <p>Written By {blog.author}</p>
                </div>
            ))}
        </section>
    )
}

export default Bloglist;
