//import { useState } from "react";

import { useState } from "react";

const ShortLink = ( { list, copy} ) => {
    const [copied, setCopied] = useState(false)

    return ( 
        <>
            {list.map((link) => (
                <div key={link.key}>
                    <p>{link.full}</p>
                    <p>{link.short}</p>
                    <p>{link.copied}</p>
                    {link.copied ? <button onClick={() => {copy(link.short)}}>copied</button> : <button onClick={() => {copy(link.short); link.copied = true}}>copy</button>}
                </div>
            ))}
        </>
    );
}

export default ShortLink;
