import { useState, usw } from 'react' //using state hooks
// using a functio that receives arguments
// <button onClick={() => {hello("joe")}}>Click</button>

const Homepage = () => {
    //let clickBtn = document.getElementById("getBtn");
    function hello (name){
        console.log(name);
    }
    // setting a reactive element
    let [name, setName] = useState("name");
    let [age, setAge] = useState(0);
    // function to display the user input
    function dispName (){
        let nameInput = document.getElementById("name").value;
        let ageInput = document.getElementById("age").value;
        // changing the initial value to users inputs
        name = setName(nameInput);
        age = setAge(ageInput);
    }

    useEffect(() => {
        console.log("God is great")
    })

    return(
        <main>
            <h1>React</h1>
            <button onClick={() => {hello("joe")}}>Click</button>
            <p>your name and age?</p>
            <input type="text" name="name" id="name" />
            <input type="text" name="age" id="age" />
            <input type="submit" value="enter" onClick = {dispName}/>
            <p> {name} is {age} years old</p>
        </main>
    )
}

export default Homepage
