import { useState } from "react";

function Blog(){
    // creating an array of objects with use states
    let [blogs, setBlog] = useState([
        {title: "first blog post", body: "lorem blah blah blah", author: "Joseph", id: 1},
        {title: "Second blog post", body: "lorem blah blah blah", author: "Richard", id: 2},
        {title: "third blog post", body: "lorem blah blah blah", author: "Simeon", id: 1}
    ]);
    return(
        //using map function to it
        <article >
            {blogs.map((blog) => (
                <div className="container" key={blog.id}>
                    <h2>{blog.title}</h2>
                    <p>Written By {blog.author}</p>
                </div>
            ))}
        </article>
    )
}

export default Blog;
