import { useEffect, useState } from "react";

// creating custom hook to fecth data from giving api url
function useFetch(url) {
    // creating an array of objects with use states
    let [datas, setDatas] = useState(null);

    // creating variable to add loading message
    let [isloaded, setIsloaded] = useState(true);

    // creating usestate variable to display error on browser page
    let [errors, setErrors] = useState(null);

     // passing array of state change to trigger use Effect
     useEffect(() => {
        console.log("Use effect ran")
        setTimeout(() => {
            fetch(url)
            .then((res) => {
                // throwing self error if not able to fetch data from api
                if(!res.ok)
                {
                    throw Error('could not fetch data from api');
                }
                return res.json();
            })
            .then((data) => {
                setIsloaded(false);
                setDatas(data);
                setErrors(null);
            })
            .catch((error) => {
                console.log(error);
                setErrors(error.message);
                setIsloaded(false); // so as not to display loading when there is an error
            });
        }, 2000);

    }, [url])
    //returning an object of values
    return {
        datas, isloaded, errors
    }
}

export default useFetch;
