import { useState } from "react";
//import ShortLink from "./ShortLink";

const FormLink = () => {
    const [inputLink, setInputLink] = useState("");
    //const [shortLink, setShortLink] = useState("");
    const [linkList, setLinkList] = useState([]);
    //const [fullLinkList, setFullLinkList] = useState([]);
    const objectLink = {
        short: "",
        full: ''
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        fetch(`https://api.shrtco.de/v2/shorten?url=${inputLink}`)
        .then((res) => res.json())
        .then((data) => {
            console.log(data);
            objectLink.short = data.result.short_link;
            objectLink.full = inputLink;
            //setShortLink(data.result.short_link);
            setLinkList([...linkList, objectLink]);
            console.log(linkList)
            //setFullLinkList([...fullLinkList, inputLink]);
        });
    }
    return ( 
        <section>
            <form onSubmit={handleSubmit}>
                <input 
                required
                type="text" 
                placeholder="Shorten a link here... " 
                value={inputLink}
                onChange={(e) => setInputLink(e.target.value)}/>
                <input type="submit" value="Shorten it!" />
            </form>
            {/* <ShortLink shortList={linkList} fullList={fullLinkList}/> */}
        </section>
     );
}

export default FormLink;
